INSERT INTO `chitietphim` (`id`, `gioithieu`, `vote`, `poster`, `status`, `ngayphathanh`, `imdb`, `trailer`, `tagline`, `anhbia`, `thoiluong`, `sotap`, `hot`, `uutien`, `tukhoa`) VALUES
(91, '<p><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\"><strong>Người dịch: Mai Khanh</strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Trong d&ograve;ng lịch sử của kh&ocirc;ng gian, thời gian v&agrave; truyền h&igrave;nh, kh&ocirc;ng c&oacute; thứ g&igrave; giống với <em>Doctor Who</em>, loạt phim khoa học viễn tưởng của Anh sẽ kỷ niệm 50 năm ph&aacute;t s&oacute;ng v&agrave;o năm nay, t&iacute;nh theo năm tr&aacute;i đất. Chỉ c&oacute; <em>Star Trek</em> l&agrave; gần đạt mức n&agrave;y ở mặt sức sống, nhưng cũng chẳng thể n&agrave;o đến gần được.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Thứ l&agrave;m <em>Doctor Who</em> nổi bật ch&iacute;nh l&agrave;, kh&ocirc;ng t&iacute;nh đến khoảng c&aacute;ch từ thời c&aacute;c tập phim chỉ c&oacute; sơn v&agrave; giấy carton, phi thuyền treo tr&ecirc;n d&acirc;y ng&agrave;y đầu đến những s&aacute;ng tạo đẹp v&agrave; tuyệt vời kh&ocirc;ng ngừng nghỉ ng&agrave;y nay, loạt phim hiện giờ với loạt bắt đầu tr&ecirc;n BBC năm 1963 l&agrave; c&ugrave;ng một loạt phim &ndash; kh&ocirc;ng phải phần sau, kh&ocirc;ng phải l&agrave;m lại, m&agrave; vẫn l&agrave; chương tr&igrave;nh đ&oacute;.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/Admin/chucnang/sanpham/img/bachtuot.jpg\" style=\"height:849px; width:600px\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Phim xoay quanh c&ugrave;ng một nh&acirc;n vật: một Time Lord ngo&agrave;i tr&aacute;i đất du h&agrave;nh khắp tạo h&oacute;a trong một thứ nh&igrave;n giống buồng cảnh s&aacute;t cũ ở London, đến nay đ&atilde; do 11 diễn vi&ecirc;n thủ vai. Mỗi người mới l&agrave; một lần &ldquo;t&aacute;i sinh&rdquo; của người cũ, kh&aacute;c m&agrave; giống, giống m&agrave; kh&aacute;c. D&ugrave; l&agrave; một t&ecirc;n hề, một g&atilde; đỏm d&aacute;ng, một qu&yacute; tộc hay một &ocirc;ng gi&aacute;o, t&oacute;c xoăn hay t&oacute;c thẳng, trẻ hay gi&agrave;, đi&ecirc;n kh&ugrave;ng hay trung dung, thấp hay cao (d&ugrave; phần lớn l&agrave; cao), họ đều l&agrave; c&ugrave;ng một người.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Đ&oacute; l&agrave; một ph&aacute;t minh bắt nguồn từ việc Doctor đầu ti&ecirc;n, William Hartnell dừng bước sau ba m&ugrave;a phim: khoảnh khắc một Hartnell giọng cao quyền qu&yacute; được một Patrick Troughton chậm hiểu hơn v&agrave; tươi vui tiếp bước ch&iacute;nh l&agrave; khoảnh khắc đ&atilde; h&ograve;a trộn tinh t&uacute;y của nh&acirc;n vật v&agrave; loạt phim &ndash; v&igrave;, cũng như ch&iacute;nh Doctor, <em>Doctor Who </em>&ldquo;chết&rdquo; để m&agrave; sống tiếp, theo chu kỳ bỏ đi sự ph&aacute;t triển gi&agrave; cỗi cho những cảnh mới nảy mầm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Phim sống s&oacute;t qua cả một khoảng thời gian d&agrave;i &ldquo;h&oacute;a đ&aacute;&rdquo;, từ khi bị hủy năm 1989 tới đợt hồi sinh năm 2005, giữa khoảng thời gian n&agrave;y c&oacute; một phim d&agrave;i truyền h&igrave;nh (bước thử nghiệm thất bại) năm 1996 v&agrave; được giữ sinh tồn th&ocirc;ng qua một chuỗi truyện v&agrave; phim tr&ecirc;n s&oacute;ng ph&aacute;t thanh kể về nhiều Doctor v&agrave; bạn đồng h&agrave;nh cũ kh&aacute;c nhau &ndash; nh&acirc;n vật n&agrave;y c&oacute; th&oacute;i quen chọn một v&agrave;i bạn đồng h&agrave;nh đi chu du c&ugrave;ng, thường l&agrave; người Anh tr&ecirc;n tr&aacute;i đất &ndash; đến h&ocirc;m nay vẫn tiếp tục đi c&ugrave;ng loạt phim hiện giờ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Tầm ảnh hưởng văn h&oacute;a của<em> Doctor Who</em> ở Anh s&acirc;u v&agrave; rộng, ở đ&oacute; tất cả học sinh đều biết TARDIS hoặc Dalek l&agrave; g&igrave;. (Nếu bạn chưa biết, th&igrave; đ&oacute; l&agrave; chiếc m&aacute;y kh&ocirc;ng gian-thời gian v&agrave; kẻ th&ugrave; lớn nhất của Doctor, theo thứ tự.)</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">C&oacute; một cảm gi&aacute;c sở hữu, v&agrave; đam m&ecirc; được nhiều thế hệ chia sẻ: người Anh d&ugrave;ng cụm &ldquo;Doctor của t&ocirc;i&rdquo; để chỉ phi&ecirc;n bản m&agrave; họ xem từ nhỏ, hoặc dần y&ecirc;u th&iacute;ch nhất. (Phim khoa học viễn tưởng hay kết hợp được người trẻ v&agrave; gi&agrave;: cho ph&eacute;p người lớn đ&ugrave;a như trẻ nhỏ, v&agrave; đối xử với trẻ nhỏ như những người trưởng th&agrave;nh.) V&agrave; c&oacute; thể kh&ocirc;ng k&eacute;m phần quan trọng, loạt phim n&agrave;y đ&atilde; cho nước Anh vị tr&iacute; trung t&acirc;m trong c&aacute;c sự kiện tr&ecirc;n vũ trụ: Doctor c&oacute; thể đến từ h&agrave;nh tinh Gallifrey, nhưng lu&ocirc;n n&oacute;i giọng Anh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">M&atilde;i đến thập ni&ecirc;n 1970 loạt phim n&agrave;y mới đến Mỹ, gần như tr&ugrave;ng với thời gian đầu d&agrave;nh cho Doctor số 4 của Tom Baker &ndash; với kiểu mắt v&agrave; t&oacute;c của Harpo Marx, chiếc n&oacute;n rộng v&agrave;nh v&agrave; c&aacute;i khăn d&agrave;i đến mức qu&aacute;i gở, ng&agrave;y xưa đ&acirc;y l&agrave; Doctor nổi tiếng nhất với người Mỹ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Từ đ&oacute; một tr&agrave;o lưu đ&atilde; được th&agrave;nh lập &ndash; c&oacute; c&aacute;c hội thảo Doctor Who ở Mỹ từ năm 1979 đến nay &ndash; nhưng phải đến đợt hồi sinh ở thế kỷ 21 th&igrave; loạt phim n&agrave;y mới thực sự t&igrave;m được nơi neo đậu, đầu ti&ecirc;n l&agrave; tr&ecirc;n mạng lưới c&oacute; t&ecirc;n Sci-Fi Channel v&agrave; giờ l&agrave; tr&ecirc;n BBC America.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Điều h&agrave;nh ch&iacute;nh của loạt phim trước đ&acirc;y l&agrave; Russell T. Davies, người đ&atilde; hồi sinh loạt phim v&agrave; cũng l&agrave; người h&acirc;m mộ từ nhỏ, đ&atilde; l&agrave;m việc trong lĩnh vực truyền h&igrave;nh cho trẻ em v&agrave; tạo n&ecirc;n loạt <em>Queer as Folk</em> của ch&iacute;nh &ocirc;ng, phản &aacute;nh được phạm vi thấu hiểu &ocirc;ng mang v&agrave;o loạt phim n&agrave;y. Phi&ecirc;n bản của &ocirc;ng h&ograve;a nhịp với truyền thống v&agrave; chi tiết của chương tr&igrave;nh cũ, đến mức m&agrave; thậm ch&iacute; nhịp của bản nhạc đầu phim cũng được biến th&agrave;nh chi tiết trong kịch bản.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">&Ocirc;ng kh&ocirc;ng thay đổi loạt phim n&agrave;y nhiều v&igrave; &ocirc;ng đ&atilde; lặn s&acirc;u v&agrave; mang l&ecirc;n bề mặt những g&igrave; phim lu&ocirc;n chứa đựng, hoặc &iacute;t nhất cũng h&agrave;m &yacute;: sự l&atilde;ng mạn, nỗi &aacute;m ảnh, sự h&agrave;i hước, sự quyến rũ, v&agrave; t&iacute;nh thơ.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">&Ocirc;ng cũng đ&atilde; cho TARDIS một linh hồn v&agrave;, quan trọng hơn hết, kh&ocirc;ng chỉ biến Doctor th&agrave;nh người sống s&oacute;t cuối c&ugrave;ng của giống lo&agrave;i n&agrave;y (hay Doctor tin l&agrave; vậy) m&agrave; c&ograve;n th&agrave;nh c&ocirc;ng cụ cho sự t&agrave;n lụi cần thiết; Doctor của &ocirc;ng l&agrave; một người bị &aacute;m ảnh. Nh&acirc;n vật n&agrave;y, người lu&ocirc;n chạy kh&ocirc;ng &iacute;t th&igrave; nhiều &ndash; thực ra, từ đầu ti&ecirc;n Doctor số 9 n&oacute;i l&agrave; &ldquo;Chạy!&rdquo; &ndash; cũng đang chạy trốn ch&iacute;nh m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Doctor l&agrave; người cứu thế v&agrave; cũng l&agrave; mối đe dọa, t&ugrave;y bạn đứng b&ecirc;n n&agrave;o &ndash; anh kh&ocirc;ng mang nhiệm vụ tr&ecirc;n m&igrave;nh nhưng khi nhiệm vụ cứ t&igrave;m đến anh. Những Doctor của thế kỷ 21 &mdash; Christopher Eccleston, David Tennant v&agrave; Matt Smith &mdash; đều thể hiện nh&acirc;n vật n&agrave;y, d&ugrave; theo nhiều c&aacute;ch kh&aacute;c nhau (dữ dằn, thu h&uacute;t, qu&aacute;i đản), với một chiều s&acirc;u của người đ&atilde; gần ngh&igrave;n tuổi, từng thấy được những g&igrave; đẹp nhất v&agrave; xấu xa nhất của vũ trụ n&agrave;y, từ th&aacute;i cực n&agrave;y đến th&aacute;i cực kh&aacute;c, từ l&uacute;c hồng hoang đến ng&agrave;y chung cuộc.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Mỗi đợt t&aacute;i sinh của Doctor đều l&agrave; l&uacute;c chia tay, v&agrave; c&ograve;n những đợt chia tay với c&aacute;c bạn đồng h&agrave;nh đến rồi đi nữa. Nỗi &acirc;u lo v&agrave; hứng th&uacute; đi c&ugrave;ng với những khoảnh khắc đ&oacute; v&agrave;o năm 2009 c&ograve;n được nh&acirc;n l&ecirc;n nhiều lần khi Davies giao quyền điều h&agrave;nh lại cho Steven Moffat, &ocirc;ng mang đến những vai ch&iacute;nh mới &ndash; Smith, Doctor hiện tại, ngớ ngẩn v&agrave; đen tối, c&ugrave;ng Karen Gillan, bạn đồng h&agrave;nh đầu ti&ecirc;n của anh, v&agrave; nay đ&atilde; l&agrave; qu&aacute; khứ &ndash; v&agrave; một kiểu suy nghĩ &ldquo;xoắn n&atilde;o&rdquo; hơn; &ocirc;ng th&iacute;ch tạo n&ecirc;n những b&agrave;i to&aacute;n phức tạp với thời gian, để tạo kịch t&iacute;nh hoặc h&agrave;i hước.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Vẫn c&ograve;n những người than thở v&igrave; mất đi Davies v&agrave; Doctor số 10 của Tennant, anh c&ugrave;ng với bạn đồng h&agrave;nh Rose Tyler, do Billie Piper thủ vai, đ&atilde; mang đến cho phim n&agrave;y chuyện t&igrave;nh đầy đủ (d&ugrave; kh&ocirc;ng trọn vẹn) đầu ti&ecirc;n, nhưng những g&igrave; Moffat đ&atilde; l&agrave;m cũng c&oacute; t&iacute;nh cảm động ri&ecirc;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Nhưng ch&iacute;nh sự mất m&aacute;t l&agrave; cốt l&otilde;i của loạt phim n&agrave;y &ndash; chắc chắn ở đ&acirc;y c&oacute; nhiều giọt nước mắt tu&ocirc;n tr&agrave;n hoặc được nuốt ngược v&agrave;o l&ograve;ng nhiều hơn ở bất kỳ loạt phim khoa học viễn tưởng n&agrave;o kh&aacute;c &ndash; v&agrave; c&oacute; những tập kh&oacute; m&agrave; xem lại nếu kh&ocirc;ng đang trong t&acirc;m trạng muốn kh&oacute;c &ograve;a. Sự mất m&aacute;t &ndash; v&agrave; mối quan hệ mất c&ugrave;ng với n&oacute; &ndash; l&agrave; thứ tạo n&ecirc;n sức mạnh cho <em>Doctor Who</em>.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:14px\">Sự bẻ cong thời gian cho c&aacute;c c&acirc;u chuyện một yếu tố định mệnh; đ&acirc;y l&agrave; loạt phim về những người lẽ ra phải ở b&ecirc;n nhau. V&agrave; ph&iacute;a sau tất cả sự ồn &agrave;o đấu đ&aacute;, ngờ hoặc, kinh dị v&agrave; vui vẻ, vọng l&ecirc;n một c&acirc;u chuyện về t&igrave;nh y&ecirc;u, t&igrave;nh bạn v&agrave; sự gắn kết m&agrave; thậm ch&iacute; những bờ bến xa vời nhất của kh&ocirc;ng gian v&agrave; thời gian cũng kh&ocirc;ng thể chia cắt.</span></span></p>\r\n', '173', '/Admin/chucnang/sanpham/img/bachtuot.jpg', '', '', '0', '', '', '', '', 0, 1, 0, 'Doctor Who: 50 Năm Tuyệt Vời Xuyên Không Gian Và Thời Gian, whovian viet nam, doctor who fc in viet nam'),
