INSERT INTO `chitietphim` (`id`, `gioithieu`, `vote`, `poster`, `status`, `ngayphathanh`, `imdb`, `trailer`, `tagline`, `anhbia`, `thoiluong`, `sotap`, `hot`, `uutien`, `tukhoa`) VALUES
(145, '<p>Một h&agrave;nh tinh b&igrave;nh thường, kh&ocirc;ng c&oacute; g&igrave; đặc biệt bỗng nhi&ecirc;n được nửa vũ trụ săn đ&oacute;n. Nguy&ecirc;n nh&acirc;n l&agrave; v&igrave; từ b&ecirc;n trong h&agrave;nh tinh n&agrave;y ph&aacute;t ra một lời nhắn kỳ lạ m&agrave; kh&ocirc;ng một ai c&oacute; thể hiểu nội dung. Lời nhắn lặp đi lặp lại trong kh&ocirc;ng gian, thời gian, tr&ecirc;n khắp vũ trụ. Doctor của ch&uacute;ng ta đương nhi&ecirc;n l&agrave; một trong số những kẻ t&ograve; m&ograve;. Sự việc trở n&ecirc;n nghi&ecirc;m trọng khi Doctor dịch được lời nhắn v&agrave; tất cả mọi sinh vật trong vũ trụ đều c&oacute; thể hiểu lời nhắn đ&oacute;: &quot;DOCTOR WHO? DOCTOR WHO? DOCTOR WHO?&quot;</p>\r\n\r\n<p>Phim được Upload bởi <a href=\"https://www.facebook.com/vinhvillain\" target=\"blank\">Vinh Villain</a> | Vietsub bởi <a href=\"https://www.facebook.com/vinhvillain\" target=\"blank\">Vinh Villain</a></p>\r\n', '5', 'https://static.wapmaker.net/5cfafc47fce1810f802a08d0/Mi/mv5bmty0mzqyndm0nv5bml5banbnxkftztgwmdq5nzg3mde_v1_.jpg', '', '', '0', '', '', '', '', 0, 0, 0, 'Christmas Special 2013 - The Time Of The Doctor,Tập Giáng Sinh'),
(146, '<p>Doctor Who l&agrave; loạt phim khoa học viễn tưởng, kể về một người ngo&agrave;i h&agrave;nh tinh mang t&ecirc;n The Doctor, thuộc chủng tộc Time Lord (tạm dịch: Ch&uacute;a tể Thời gian) đến từ h&agrave;nh tinh Gallifrey. Loạt phim xoay quanh những cuộc phi&ecirc;u lưu của Doctor v&agrave; c&aacute;c bạn đồng h&agrave;nh chu du khắp thời gian v&agrave; kh&ocirc;ng gian trong một t&agrave;u vũ trụ mang t&ecirc;n TARDIS. c&oacute; những người bạn đồng h&agrave;nh rất vui t&iacute;nh v&agrave; dễ mến... Trong c&aacute;c chuyến phi&ecirc;u lưu của m&igrave;nh, Doctor thường c&oacute; những người bạn đồng h&agrave;nh rất vui t&iacute;nh v&agrave; dễ mến... Series phim đ&atilde; đạt kỷ lục thế giới l&agrave; series phim truyền h&igrave;nh khoa học viễn tưởng d&agrave;i nhất thế giới v&agrave; cũng l&agrave; Series phim khoa học viễn tưởng th&agrave;nh c&ocirc;ng nhất mọi thời đại, đồng thời cũng l&agrave; series phim l&agrave; ti&ecirc;n phong trong việc sử dụng kĩ xảo điện ảnh.</p>\r\n\r\n<p>Phim được Upload bởi <a href=\"https://www.facebook.com/vinhvillain\" target=\"blank\">Vinh Villain</a> | Vietsub bởi <a href=\"https://www.facebook.com/vinhvillain\" target=\"blank\">Vinh Villain</a></p>\r\n', '10', 'https://static.wapmaker.net/5cfafc47fce1810f802a08d0/Mi/81ybltjrwjl_sx342_.jpg', '', '', '0', '', '', '', '', 0, 0, 0, 'Christmas Special 2014 - Last Christmas,Tập Giáng Sinh'),
